"use client"

import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom"
import { useState, useEffect } from "react"
import { ThemeProvider } from "./context/ThemeContext"
import { ToastContainer } from "react-toastify"
import LandingPage from "./pages/LandingPage/LandingPage"
import StudentDashboard from "./pages/Dashboards/StudentDashboard"
import FacultyDashboard from "./pages/Dashboards/FacultyDashboard"
import AdminDashboard from "./pages/Dashboards/AdminDashboard"
import CourseMaterials from "./components/Course/CourseMaterials"
import UpdatePlacement from "./components/Placement/UpdatePlacement"
import Profile from "./pages/Profile/Profile"
import AuthContext from "./context/AuthContext"
import ProtectedRoute from "./components/Auth/ProtectedRoute"
import "bootstrap/dist/css/bootstrap.min.css"
import "react-toastify/dist/ReactToastify.css"
import "./index.css"

// Import additional pages
import CourseDetails from "./pages/Course/CourseDetails"
import AssignmentDetails from "./pages/Assignment/AssignmentDetails"
import EventDetails from "./pages/Event/EventDetails"
import ManageCourses from "./pages/Course/ManageCourses"
import ManageStudents from "./pages/Student/ManageStudents"
import Notifications from "./pages/Notifications/Notifications"
import UserDetails from "./pages/User/UserDetails"
import NotFound from "./pages/NotFound"

function App() {
  const [user, setUser] = useState(null)
  const [isAuthenticated, setIsAuthenticated] = useState(false)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const storedUser = localStorage.getItem("user")
    const token = localStorage.getItem("token")

    if (storedUser && token) {
      setUser(JSON.parse(storedUser))
      setIsAuthenticated(true)
    }

    setLoading(false)
  }, [])

  const login = (userData, token) => {
    localStorage.setItem("user", JSON.stringify(userData))
    localStorage.setItem("token", token)
    setUser(userData)
    setIsAuthenticated(true)
  }

  const logout = () => {
    localStorage.removeItem("user")
    localStorage.removeItem("token")
    setUser(null)
    setIsAuthenticated(false)
  }

  if (loading) {
    return <div className="loading-screen">Loading...</div>
  }

  return (
    <AuthContext.Provider value={{ user, isAuthenticated, login, logout }}>
      <ThemeProvider>
        <Router>
          <Routes>
            <Route path="/" element={<LandingPage />} />

            {/* Dashboard Routes */}
            <Route
              path="/student-dashboard"
              element={
                <ProtectedRoute role="student">
                  <StudentDashboard />
                </ProtectedRoute>
              }
            />
            <Route
              path="/faculty-dashboard"
              element={
                <ProtectedRoute role="faculty">
                  <FacultyDashboard />
                </ProtectedRoute>
              }
            />
            <Route
              path="/admin-dashboard"
              element={
                <ProtectedRoute role="admin">
                  <AdminDashboard />
                </ProtectedRoute>
              }
            />

            {/* Course Routes */}
            <Route
              path="/course/:courseId"
              element={
                <ProtectedRoute>
                  <CourseDetails />
                </ProtectedRoute>
              }
            />
            <Route
              path="/course/:courseId/materials"
              element={
                <ProtectedRoute>
                  <CourseMaterials />
                </ProtectedRoute>
              }
            />
            <Route
              path="/manage-courses"
              element={
                <ProtectedRoute roles={["admin", "faculty"]}>
                  <ManageCourses />
                </ProtectedRoute>
              }
            />

            {/* Assignment Routes */}
            <Route
              path="/assignment/:assignmentId"
              element={
                <ProtectedRoute>
                  <AssignmentDetails />
                </ProtectedRoute>
              }
            />

            {/* Event Routes */}
            <Route
              path="/event/:eventId"
              element={
                <ProtectedRoute>
                  <EventDetails />
                </ProtectedRoute>
              }
            />
            <Route
              path="/events"
              element={
                <ProtectedRoute>
                  <EventDetails />
                </ProtectedRoute>
              }
            />

            {/* Student Routes */}
            <Route
              path="/manage-students"
              element={
                <ProtectedRoute roles={["admin", "faculty"]}>
                  <ManageStudents />
                </ProtectedRoute>
              }
            />
            <Route
              path="/student/:studentId"
              element={
                <ProtectedRoute roles={["admin", "faculty"]}>
                  <UserDetails />
                </ProtectedRoute>
              }
            />

            {/* User Routes */}
            <Route
              path="/user/:userId"
              element={
                <ProtectedRoute roles={["admin"]}>
                  <UserDetails />
                </ProtectedRoute>
              }
            />

            {/* Placement Routes */}
            <Route
              path="/placements"
              element={
                <ProtectedRoute>
                  <UpdatePlacement />
                </ProtectedRoute>
              }
            />

            {/* Notification Routes */}
            <Route
              path="/notifications"
              element={
                <ProtectedRoute>
                  <Notifications />
                </ProtectedRoute>
              }
            />

            {/* Profile Route */}
            <Route
              path="/profile"
              element={
                <ProtectedRoute>
                  <Profile />
                </ProtectedRoute>
              }
            />

            {/* Redirect to appropriate dashboard if logged in */}
            <Route
              path="/login"
              element={
                isAuthenticated ? <Navigate to={`/${user.role}-dashboard`} replace /> : <Navigate to="/" replace />
              }
            />

            {/* 404 Route */}
            <Route path="*" element={<NotFound />} />
          </Routes>
          <ToastContainer
            position="top-right"
            autoClose={3000}
            hideProgressBar={false}
            newestOnTop
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
          />
        </Router>
      </ThemeProvider>
    </AuthContext.Provider>
  )
}

export default App
