import { useState } from 'react';
import { Modal, Form, Button, Alert, Row, Col } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { authService } from '../../services/api';
import './AuthModal.css';

const AuthModal = ({ show, onHide }) => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true); // Toggle between Login and Signup
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student');
  const [error, setError] = useState('');

  const handleToggle = () => {
    setIsLogin(!isLogin);
    setError(''); // Clear error on toggle
    setName('');
    setEmail('');
    setPassword('');
    setRole('student');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await authService.login({ email, password });
        localStorage.setItem('token', res.data.token);
        const { role } = res.data.user;
        onHide(); // Close modal on successful login
        if (role === 'student') {
          navigate('/student-dashboard');
        } else if (role === 'faculty') {
          navigate('/faculty-dashboard');
        } else if (role === 'admin') {
          navigate('/admin-dashboard');
        }
      } else {
        await authService.register({ name, email, password, role });
        setIsLogin(true); // Switch to login after successful signup
        setError(''); // Clear any previous errors
      }
    } catch (err) {
      setError(err.response?.data?.message || (isLogin ? 'Login failed' : 'Signup failed'));
    }
  };

  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton className="border-0 pb-0">
        <Modal.Title className="w-100 text-center">
          {isLogin ? 'Login to Smart Campus' : 'Sign Up for Smart Campus'}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body className="pt-0">
        <div className="d-flex justify-content-center mb-3">
          <div className="toggle-switch">
            <span className={`toggle-label ${isLogin ? 'active' : ''}`}>Login</span>
            <label className="switch">
              <input type="checkbox" checked={!isLogin} onChange={handleToggle} />
              <span className="slider round"></span>
            </label>
            <span className={`toggle-label ${!isLogin ? 'active' : ''}`}>Sign Up</span>
          </div>
        </div>
        {error && <Alert variant="danger" className="text-center">{error}</Alert>}
        <Form onSubmit={handleSubmit}>
          {!isLogin && (
            <Form.Group className="mb-3" controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                className="rounded-3"
              />
            </Form.Group>
          )}
          <Form.Group className="mb-3" controlId="formEmail">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="rounded-3"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="rounded-3"
            />
          </Form.Group>
          {!isLogin && (
            <Form.Group className="mb-3" controlId="formRole">
              <Form.Label>Role</Form.Label>
              <Form.Select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="rounded-3"
              >
                <option value="student">Student</option>
                <option value="faculty">Faculty</option>
                <option value="admin">Admin</option>
              </Form.Select>
            </Form.Group>
          )}
          <Button
            variant="primary"
            type="submit"
            className="w-100 rounded-3 mt-2"
          >
            {isLogin ? 'Login' : 'Sign Up'}
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default AuthModal;
