"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { Container, Row, Col, Card, Table, Button, Badge, ProgressBar } from "react-bootstrap"
import { motion } from "framer-motion"
import { getCurrentUser } from "../../utils/auth"
import Navbar from "../../components/Layout/Navbar"
import Footer from "../../components/Layout/Footer"

const StudentDashboard = () => {
  const navigate = useNavigate()
  const user = getCurrentUser()
  const [dashboardData, setDashboardData] = useState({
    courses: [],
    events: [],
    attendance: [],
    grades: [],
    placements: [],
    notifications: [],
    stats: {
      totalCourses: 0,
      averageGrade: 0,
      attendanceRate: 0,
      completedAssignments: 0,
    },
  })
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    if (!user || user.role !== "student") {
      navigate("/login")
      return
    }

    const fetchData = async () => {
      try {
        // Mock data for demonstration - replace with actual API calls
        const mockData = {
          courses: [
            {
              _id: "1",
              title: "Introduction to Programming",
              instructor: "Dr. Smith",
              progress: 75,
              students: [user._id],
            },
            { _id: "2", title: "Data Structures", instructor: "Prof. Johnson", progress: 60, students: [user._id] },
            { _id: "3", title: "Web Development", instructor: "Dr. Brown", progress: 90, students: [user._id] },
          ],
          events: [
            {
              _id: "1",
              title: "Tech Workshop",
              date: "2025-06-20",
              description: "Learn modern technologies",
              rsvped: false,
            },
            { _id: "2", title: "Career Fair", date: "2025-06-25", description: "Meet top companies", rsvped: true },
            {
              _id: "3",
              title: "Coding Competition",
              date: "2025-07-01",
              description: "Show your skills",
              rsvped: false,
            },
          ],
          attendance: [
            { _id: "1", course: "Introduction to Programming", percentage: 85 },
            { _id: "2", course: "Data Structures", percentage: 78 },
            { _id: "3", course: "Web Development", percentage: 92 },
          ],
          grades: [
            { _id: "1", course: "Introduction to Programming", grade: "A", semester: "Spring 2025" },
            { _id: "2", course: "Data Structures", grade: "B+", semester: "Spring 2025" },
            { _id: "3", course: "Web Development", grade: "A-", semester: "Spring 2025" },
          ],
          placements: [
            { _id: "1", company: "Tech Corp", role: "Software Engineer", status: "Applied" },
            { _id: "2", company: "Innovation Labs", role: "Frontend Developer", status: "Interview Scheduled" },
          ],
          notifications: [
            { _id: "1", message: "New assignment posted in Web Development", read: false, type: "assignment" },
            { _id: "2", message: "Career Fair registration is now open", read: false, type: "event" },
            { _id: "3", message: "Grade updated for Data Structures", read: true, type: "grade" },
          ],
        }

        setDashboardData({
          ...mockData,
          stats: {
            totalCourses: mockData.courses.length,
            averageGrade: 3.7,
            attendanceRate: Math.round(
              mockData.attendance.reduce((acc, att) => acc + att.percentage, 0) / mockData.attendance.length,
            ),
            completedAssignments: 12,
          },
        })
      } catch (err) {
        console.error(err)
      } finally {
        setLoading(false)
      }
    }

    fetchData()
  }, [user, navigate])

  const handleRSVP = async (eventId) => {
    try {
      setDashboardData((prev) => ({
        ...prev,
        events: prev.events.map((event) => (event._id === eventId ? { ...event, rsvped: true } : event)),
      }))
    } catch (err) {
      console.error(err)
    }
  }

  const getGradeColor = (grade) => {
    if (grade.startsWith("A")) return "success"
    if (grade.startsWith("B")) return "info"
    if (grade.startsWith("C")) return "warning"
    return "danger"
  }

  const getAttendanceColor = (percentage) => {
    if (percentage >= 85) return "success"
    if (percentage >= 75) return "warning"
    return "danger"
  }

  if (loading) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center animated-bg">
        <div className="text-center">
          <div className="loading-spinner mb-3" style={{ width: "3rem", height: "3rem" }}></div>
          <h4 className="text-white">Loading your dashboard...</h4>
        </div>
      </div>
    )
  }

  return (
    <>
      <Navbar />
      <div className="min-vh-100 animated-bg">
        <Container fluid className="py-5">
          {/* Welcome Section */}
          <Row className="mb-5">
            <Col>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8 }}
                className="text-center"
              >
                <h1 className="display-4 fw-bold text-white mb-3">Welcome back, {user.name}! ðŸ‘‹</h1>
                <p className="lead text-white opacity-75">Here's what's happening in your academic journey</p>
              </motion.div>
            </Col>
          </Row>

          {/* Stats Cards */}
          <Row className="g-4 mb-5">
            {[
              { icon: "ðŸ“š", number: dashboardData.stats.totalCourses, label: "Active Courses", color: "#4facfe" },
              { icon: "ðŸ“Š", number: `${dashboardData.stats.averageGrade}/4.0`, label: "Average GPA", color: "#43e97b" },
              {
                icon: "ðŸ“…",
                number: `${dashboardData.stats.attendanceRate}%`,
                label: "Attendance Rate",
                color: "#fa709a",
              },
              {
                icon: "âœ…",
                number: dashboardData.stats.completedAssignments,
                label: "Assignments Done",
                color: "#667eea",
              },
            ].map((stat, index) => (
              <Col lg={3} md={6} key={index}>
                <motion.div
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                >
                  <Card className="stats-card hover-lift">
                    <div className="stats-icon">{stat.icon}</div>
                    <div className="stats-number">{stat.number}</div>
                    <div className="stats-label">{stat.label}</div>
                  </Card>
                </motion.div>
              </Col>
            ))}
          </Row>

          {/* Main Content */}
          <Row className="g-4">
            {/* Courses */}
            <Col lg={6}>
              <motion.div
                initial={{ opacity: 0, x: -30 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.6 }}
              >
                <Card className="modern-card h-100">
                  <Card.Header>
                    <h5 className="mb-0">ðŸ“š My Courses</h5>
                  </Card.Header>
                  <Card.Body>
                    {dashboardData.courses.map((course, index) => (
                      <div key={course._id} className="mb-4">
                        <div className="d-flex justify-content-between align-items-center mb-2">
                          <h6 className="mb-0">{course.title}</h6>
                          <small className="text-muted">{course.progress}%</small>
                        </div>
                        <p className="text-muted small mb-2">Instructor: {course.instructor}</p>
                        <ProgressBar now={course.progress} className="progress-modern mb-2" style={{ height: "8px" }} />
                        <Button
                          variant="outline-primary"
                          size="sm"
                          onClick={() => navigate(`/course/${course._id}/materials`)}
                        >
                          View Materials
                        </Button>
                      </div>
                    ))}
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>

            {/* Upcoming Events */}
            <Col lg={6}>
              <motion.div initial={{ opacity: 0, x: 30 }} animate={{ opacity: 1, x: 0 }} transition={{ duration: 0.6 }}>
                <Card className="modern-card h-100">
                  <Card.Header>
                    <h5 className="mb-0">ðŸŽ‰ Upcoming Events</h5>
                  </Card.Header>
                  <Card.Body>
                    {dashboardData.events.map((event) => (
                      <div
                        key={event._id}
                        className="d-flex justify-content-between align-items-center mb-3 p-3 rounded-3"
                        style={{ backgroundColor: "#f8f9fa" }}
                      >
                        <div>
                          <h6 className="mb-1">{event.title}</h6>
                          <small className="text-muted">{new Date(event.date).toLocaleDateString()}</small>
                          <p className="mb-0 small">{event.description}</p>
                        </div>
                        <Button
                          variant={event.rsvped ? "success" : "primary"}
                          size="sm"
                          onClick={() => handleRSVP(event._id)}
                          disabled={event.rsvped}
                        >
                          {event.rsvped ? "âœ… RSVPed" : "RSVP"}
                        </Button>
                      </div>
                    ))}
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>

            {/* Attendance */}
            <Col lg={6}>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.2 }}
              >
                <Card className="modern-card">
                  <Card.Header>
                    <h5 className="mb-0">ðŸ“Š Attendance Tracker</h5>
                  </Card.Header>
                  <Card.Body>
                    <Table className="modern-table">
                      <thead>
                        <tr>
                          <th>Course</th>
                          <th>Attendance</th>
                          <th>Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {dashboardData.attendance.map((record) => (
                          <tr key={record._id}>
                            <td>{record.course}</td>
                            <td>{record.percentage}%</td>
                            <td>
                              <Badge className={`badge-${getAttendanceColor(record.percentage)}-gradient`}>
                                {record.percentage >= 85 ? "Excellent" : record.percentage >= 75 ? "Good" : "Low"}
                              </Badge>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>

            {/* Grades */}
            <Col lg={6}>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.3 }}
              >
                <Card className="modern-card">
                  <Card.Header>
                    <h5 className="mb-0">ðŸŽ¯ Recent Grades</h5>
                  </Card.Header>
                  <Card.Body>
                    <Table className="modern-table">
                      <thead>
                        <tr>
                          <th>Course</th>
                          <th>Grade</th>
                          <th>Semester</th>
                        </tr>
                      </thead>
                      <tbody>
                        {dashboardData.grades.map((grade) => (
                          <tr key={grade._id}>
                            <td>{grade.course}</td>
                            <td>
                              <Badge className={`badge-${getGradeColor(grade.grade)}-gradient`}>{grade.grade}</Badge>
                            </td>
                            <td>{grade.semester}</td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>

            {/* Placements */}
            <Col lg={6}>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.4 }}
              >
                <Card className="modern-card">
                  <Card.Header>
                    <h5 className="mb-0">ðŸŽ¯ Placement Tracker</h5>
                  </Card.Header>
                  <Card.Body>
                    <Table className="modern-table">
                      <thead>
                        <tr>
                          <th>Company</th>
                          <th>Role</th>
                          <th>Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        {dashboardData.placements.map((placement) => (
                          <tr key={placement._id}>
                            <td>{placement.company}</td>
                            <td>{placement.role}</td>
                            <td>
                              <Badge
                                className={`badge-${placement.status === "Placed" ? "success" : "warning"}-gradient`}
                              >
                                {placement.status}
                              </Badge>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                    <Button className="btn-gradient mt-3" onClick={() => navigate("/placements")}>
                      Update Placement Status
                    </Button>
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>

            {/* Notifications */}
            <Col lg={6}>
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.5 }}
              >
                <Card className="modern-card">
                  <Card.Header>
                    <h5 className="mb-0">ðŸ”” Recent Notifications</h5>
                  </Card.Header>
                  <Card.Body>
                    {dashboardData.notifications.map((notification) => (
                      <div
                        key={notification._id}
                        className={`d-flex align-items-center p-3 mb-3 rounded-3 ${notification.read ? "bg-light" : "bg-primary bg-opacity-10"}`}
                      >
                        <div className="me-3">
                          {notification.type === "assignment" && "ðŸ“"}
                          {notification.type === "event" && "ðŸŽ‰"}
                          {notification.type === "grade" && "ðŸ“Š"}
                        </div>
                        <div className="flex-grow-1">
                          <p className="mb-0">{notification.message}</p>
                        </div>
                        {!notification.read && <Badge bg="primary">New</Badge>}
                      </div>
                    ))}
                  </Card.Body>
                </Card>
              </motion.div>
            </Col>
          </Row>
        </Container>
      </div>
      <Footer />
    </>
  )
}

export default StudentDashboard
