"use client"

import { useContext, useState, useEffect } from "react"
import { Link, useNavigate, useLocation } from "react-router-dom"
import { Navbar as BSNavbar, Nav, Container, Button, Dropdown, Badge } from "react-bootstrap"
import { toast } from "react-toastify"
import { BsMoon, BsSun, BsBell } from "react-icons/bs"
import AuthContext from "../../context/AuthContext"
import ThemeContext from "../../context/ThemeContext"
import AuthModal from "../Auth/AuthModal"
import { notificationService } from "../../services/api"

const Navbar = ({ onLogin, onSignup }) => {
  const navigate = useNavigate()
  const location = useLocation()
  const { isAuthenticated, user, logout } = useContext(AuthContext)
  const { theme, toggleTheme } = useContext(ThemeContext)
  const [showAuthModal, setShowAuthModal] = useState(false)
  const [authMode, setAuthMode] = useState("login")
  const [notifications, setNotifications] = useState([])
  const [unreadCount, setUnreadCount] = useState(0)
  const [showNotifications, setShowNotifications] = useState(false)

  useEffect(() => {
    if (isAuthenticated) {
      fetchNotifications()
    }
  }, [isAuthenticated])

  const fetchNotifications = async () => {
    try {
      const response = await notificationService.getUserNotifications()
      setNotifications(response.data)
      setUnreadCount(response.data.filter((notification) => !notification.read).length)
    } catch (error) {
      console.error("Failed to fetch notifications:", error)
    }
  }

  const handleShowLogin = () => {
    if (onLogin) {
      onLogin()
    } else {
      setAuthMode("login")
      setShowAuthModal(true)
    }
  }

  const handleShowSignup = () => {
    if (onSignup) {
      onSignup()
    } else {
      setAuthMode("signup")
      setShowAuthModal(true)
    }
  }

  const handleLogout = () => {
    logout()
    toast.info("You have been logged out")
    navigate("/")
  }

  const getDashboardLink = () => {
    if (!user) return "/"

    switch (user.role) {
      case "student":
        return "/student-dashboard"
      case "faculty":
        return "/faculty-dashboard"
      case "admin":
        return "/admin-dashboard"
      default:
        return "/"
    }
  }

  const handleMarkAsRead = async (notificationId) => {
    try {
      await notificationService.markAsRead(notificationId)
      setNotifications(
        notifications.map((notification) =>
          notification._id === notificationId ? { ...notification, read: true } : notification,
        ),
      )
      setUnreadCount((prev) => Math.max(0, prev - 1))
    } catch (error) {
      console.error("Failed to mark notification as read:", error)
    }
  }

  const handleMarkAllAsRead = async () => {
    try {
      await notificationService.markAllAsRead()
      setNotifications(notifications.map((notification) => ({ ...notification, read: true })))
      setUnreadCount(0)
      toast.success("All notifications marked as read")
    } catch (error) {
      console.error("Failed to mark all notifications as read:", error)
      toast.error("Failed to mark all notifications as read")
    }
  }

  const getNavLinkClass = (path) => {
    return location.pathname === path ? "nav-link fw-semibold text-primary" : "nav-link text-dark"
  }

  return (
    <>
      <BSNavbar expand="lg" className="navbar-blue py-3 shadow-sm sticky-top bg-white">
        <Container>
          <BSNavbar.Brand as={Link} to="/" className="d-flex align-items-center">
            <div
              className="me-2 d-flex align-items-center justify-content-center text-white rounded-circle"
              style={{ width: "40px", height: "40px", background: "#3b82f6" }}
            >
              <span className="fw-bold">SC</span>
            </div>
            <span className="fw-bold text-primary">Smart Campus</span>
          </BSNavbar.Brand>

          <BSNavbar.Toggle aria-controls="navbar-nav" />
          <BSNavbar.Collapse id="navbar-nav">
            <Nav className="me-auto">
              <Nav.Link as={Link} to="/" className={getNavLinkClass("/")}>
                Home
              </Nav.Link>

              {isAuthenticated && (
                <>
                  <Nav.Link as={Link} to="/courses" className={getNavLinkClass("/courses")}>
                    Courses
                  </Nav.Link>

                  {user?.role === "student" && (
                    <>
                      <Nav.Link as={Link} to="/assignments" className={getNavLinkClass("/assignments")}>
                        Assignments
                      </Nav.Link>
                      <Nav.Link as={Link} to="/attendance" className={getNavLinkClass("/attendance")}>
                        Attendance
                      </Nav.Link>
                      <Nav.Link as={Link} to="/grades" className={getNavLinkClass("/grades")}>
                        Grades
                      </Nav.Link>
                    </>
                  )}

                  {user?.role === "faculty" && (
                    <>
                      <Nav.Link as={Link} to="/manage-courses" className={getNavLinkClass("/manage-courses")}>
                        Manage Courses
                      </Nav.Link>
                      <Nav.Link as={Link} to="/manage-students" className={getNavLinkClass("/manage-students")}>
                        Manage Students
                      </Nav.Link>
                    </>
                  )}

                  {user?.role === "admin" && (
                    <>
                      <Nav.Link as={Link} to="/manage-users" className={getNavLinkClass("/manage-users")}>
                        Users
                      </Nav.Link>
                      <Nav.Link as={Link} to="/manage-courses" className={getNavLinkClass("/manage-courses")}>
                        Courses
                      </Nav.Link>
                      <Nav.Link as={Link} to="/reports" className={getNavLinkClass("/reports")}>
                        Reports
                      </Nav.Link>
                    </>
                  )}

                  <Nav.Link as={Link} to="/events" className={getNavLinkClass("/events")}>
                    Events
                  </Nav.Link>
                </>
              )}

              <Nav.Link as={Link} to="/about" className={getNavLinkClass("/about")}>
                About
              </Nav.Link>
              <Nav.Link as={Link} to="/contact" className={getNavLinkClass("/contact")}>
                Contact
              </Nav.Link>
            </Nav>

            <Nav className="d-flex align-items-center">
              <Button
                variant="link"
                className="nav-link p-2 me-2"
                onClick={toggleTheme}
                title={theme === "light" ? "Switch to dark mode" : "Switch to light mode"}
              >
                {theme === "light" ? <BsMoon size={20} /> : <BsSun size={20} />}
              </Button>

              {isAuthenticated && (
                <Dropdown className="me-3" show={showNotifications} onToggle={(isOpen) => setShowNotifications(isOpen)}>
                  <Dropdown.Toggle
                    as="div"
                    className="nav-link p-0 cursor-pointer position-relative"
                    style={{ cursor: "pointer" }}
                  >
                    <Button variant="link" className="nav-link p-2">
                      <BsBell size={20} />
                      {unreadCount > 0 && (
                        <Badge bg="danger" pill className="position-absolute top-0 end-0 translate-middle">
                          {unreadCount}
                        </Badge>
                      )}
                    </Button>
                  </Dropdown.Toggle>

                  <Dropdown.Menu
                    align="end"
                    className="notification-dropdown p-0"
                    style={{ width: "320px", maxHeight: "400px", overflowY: "auto" }}
                  >
                    <div className="d-flex justify-content-between align-items-center p-3 border-bottom">
                      <h6 className="mb-0 fw-bold">Notifications</h6>
                      {unreadCount > 0 && (
                        <Button variant="link" size="sm" className="text-primary p-0" onClick={handleMarkAllAsRead}>
                          Mark all as read
                        </Button>
                      )}
                    </div>

                    {notifications.length === 0 ? (
                      <div className="p-3 text-center text-muted">No notifications</div>
                    ) : (
                      notifications.slice(0, 5).map((notification) => (
                        <Dropdown.Item
                          key={notification._id}
                          className={`p-3 border-bottom ${!notification.read ? "bg-light" : ""}`}
                          onClick={() => handleMarkAsRead(notification._id)}
                        >
                          <div className="d-flex">
                            <div className="me-3">
                              {notification.type === "assignment" && <span className="fs-4">📝</span>}
                              {notification.type === "attendance" && <span className="fs-4">📊</span>}
                              {notification.type === "grade" && <span className="fs-4">🎓</span>}
                              {notification.type === "announcement" && <span className="fs-4">📢</span>}
                              {notification.type === "event" && <span className="fs-4">🎉</span>}
                              {notification.type === "message" && <span className="fs-4">💬</span>}
                              {notification.type === "system" && <span className="fs-4">⚙️</span>}
                            </div>
                            <div>
                              <div className="fw-semibold">{notification.title}</div>
                              <div className="small text-muted">{notification.message}</div>
                              <div className="small text-muted mt-1">
                                {new Date(notification.createdAt).toLocaleString()}
                              </div>
                            </div>
                          </div>
                        </Dropdown.Item>
                      ))
                    )}

                    <div className="p-2 text-center border-top">
                      <Button
                        variant="link"
                        size="sm"
                        className="text-primary"
                        as={Link}
                        to="/notifications"
                        onClick={() => setShowNotifications(false)}
                      >
                        View all notifications
                      </Button>
                    </div>
                  </Dropdown.Menu>
                </Dropdown>
              )}

              {isAuthenticated ? (
                <Dropdown>
                  <Dropdown.Toggle variant="outline-primary" id="dropdown-user">
                    <span className="me-2">{user?.name}</span>
                  </Dropdown.Toggle>

                  <Dropdown.Menu align="end">
                    <Dropdown.Item as={Link} to={getDashboardLink()}>
                      Dashboard
                    </Dropdown.Item>
                    <Dropdown.Item as={Link} to="/profile">
                      Profile
                    </Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item onClick={handleLogout}>Logout</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              ) : (
                <div className="d-flex gap-2">
                  <Button variant="outline-primary" onClick={handleShowLogin}>
                    Login
                  </Button>
                  <Button className="btn-blue" onClick={handleShowSignup}>
                    Sign Up
                  </Button>
                </div>
              )}
            </Nav>
          </BSNavbar.Collapse>
        </Container>
      </BSNavbar>

      <AuthModal
        show={showAuthModal}
        onHide={() => setShowAuthModal(false)}
        mode={authMode}
        switchMode={() => setAuthMode(authMode === "login" ? "signup" : "login")}
      />
    </>
  )
}

export default Navbar
